import React, { useState, useEffect } from "react";
import axios from "axios";
import { VerticalGraph } from "./VerticalGraph";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3002/allHoldings").then((res) => {
      setAllHoldings(res.data);
    });
  }, []);

  const labels = allHoldings.map((h) => h.name);

  const data = {
    labels,
    datasets: [
      {
        label: "Stock Price",
        data: allHoldings.map((h) => h.price),
        backgroundColor: "rgba(56, 126, 209, 0.6)",
      },
    ],
  };

  return (
    <div className="container-fluid px-4">
      <h3 className="mb-4">Holdings ({allHoldings.length})</h3>

      {/* TABLE CARD */}
      <div className="card shadow-sm mb-4">
        <div className="table-responsive">
          <table className="table align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th>Instrument</th>
                <th className="text-end">Qty.</th>
                <th className="text-end">Avg. cost</th>
                <th className="text-end">LTP</th>
                <th className="text-end">Cur. val</th>
                <th className="text-end">P&L</th>
                <th className="text-end">Net chg.</th>
                <th className="text-end">Day chg.</th>
              </tr>
            </thead>

            <tbody>
              {allHoldings.map((stock, index) => {
                const curValue = stock.price * stock.qty;
                const pnl = curValue - stock.avg * stock.qty;
                const isProfit = pnl >= 0;
                const dayClass = stock.isLoss ? "text-danger" : "text-success";

                return (
                  <tr key={index}>
                    <td className="fw-medium">{stock.name}</td>
                    <td className="text-end">{stock.qty}</td>
                    <td className="text-end">{stock.avg.toFixed(2)}</td>
                    <td className="text-end">{stock.price.toFixed(2)}</td>
                    <td className="text-end">{curValue.toFixed(2)}</td>
                    <td className={`text-end ${isProfit ? "text-success" : "text-danger"}`}>
                      {pnl.toFixed(2)}
                    </td>
                    <td className={`text-end ${isProfit ? "text-success" : "text-danger"}`}>
                      {stock.net}
                    </td>
                    <td className={`text-end ${dayClass}`}>
                      {stock.day}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* SUMMARY */}
      <div className="row text-center mb-5">
        <div className="col-md-4 mb-3">
          <h5>29,875.<span className="text-muted">55</span></h5>
          <p className="text-muted mb-0">Total investment</p>
        </div>
        <div className="col-md-4 mb-3">
          <h5>31,428.<span className="text-muted">95</span></h5>
          <p className="text-muted mb-0">Current value</p>
        </div>
        <div className="col-md-4 mb-3">
          <h5 className="text-success">1,553.40 (+5.20%)</h5>
          <p className="text-muted mb-0">P&L</p>
        </div>
      </div>

      {/* GRAPH */}
      <div className="card shadow-sm p-4">
        <VerticalGraph data={data} />
      </div>
    </div>
  );
};

export default Holdings;
