import React from 'react';

function LeftSection({
  imageURL,
  productName,
  productDescription,
  tryDemo,
  learnMore,
  tryDemoText = "Try Demo",
  learnMoreText = "Learn More",
  googlePlay,
  appStore
}) {
  return ( 
    <div className="container">
      <div className="row border-top mb-5" style={{ marginLeft:"90px", marginTop:"90px" }}>
        
        <div className="col-6 mt-5 p-5">
          <img src={imageURL} alt={productName} />
        </div>

        <div className="col-6" style={{ marginTop:"100px", padding:"50px 140px" }}>
          <h1 className="fs-3 text-muted">{productName}</h1>
          <p style={{ width:"95%" }}>{productDescription}</p>

          {/* âœ… CONDITIONAL LINKS WITH CUSTOM TEXT */}
          <div>
            {tryDemo && (
              <a style={{ marginRight:"40px" }} href={tryDemo}>
                {tryDemoText}
                <i className="fa fa-long-arrow-right p-2"></i>
              </a>
            )}

            {learnMore && (
              <a href={learnMore}>
                {learnMoreText}
                <i className="fa fa-long-arrow-right p-2"></i>
              </a>
            )}
          </div>

          <div className="mt-4">
            {googlePlay && (
              <a style={{ marginRight:"40px" }} href={googlePlay}>
                <img src="images/googlePlayBadge.svg" alt="Google Play" />
              </a>
            )}

            {appStore && (
              <a href={appStore}>
                <img src="images/appstoreBadge.svg" alt="App Store" />
              </a>
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default LeftSection;
