import React from 'react';

function RightSection({
  imageURL,
  productName,
  productDescription,
  learnMore
}) {
  return ( 
    <div className="container">
      <div className="row border-top" style={{ margin:"90px 40px -80px 90px" }}>
        
        <div className="col-6" style={{ marginTop:"140px", padding:"50px" }}>
          <h1 className="fs-3 mb-4 text-muted">{productName}</h1>
          <p style={{ width:"60%" }}>{productDescription}</p>

          {/* âœ… CONDITIONAL LEARN MORE */}
          {learnMore && (
            <div>
              <a href={learnMore}>
                Learn More<i className="fa fa-long-arrow-right p-2"></i>
              </a>
            </div>
          )}
        </div>

        <div className="col-6" style={{ marginTop:"40px", marginLeft:"-130px" }}>
          <img src={imageURL} alt={productName} />
        </div>

      </div>
    </div>
  );
}

export default RightSection;
