import React, { useState, useEffect } from "react";
import axios from "axios";
import { VerticalGraph } from "./VerticalGraph";

const API = process.env.REACT_APP_API_URL;

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios
      .get(`${API}/api/holdings`)
      .then((res) => {
        setAllHoldings(res.data);
      })
      .catch((err) => {
        console.error("Holdings API error:", err);
      });
  }, []);

  const labels = allHoldings.map((stock) => stock.instrument);

  const data = {
    labels,
    datasets: [
      {
        label: "Stock Price",
        data: allHoldings.map((stock) => stock.ltp),
      },
    ],
  };

  return (
    <>
      {/* ================= HOLDINGS TABLE ================= */}
      <div className="holdings-container">
        <h3 className="title">Holdings ({allHoldings.length})</h3>

        <div className="order-table">
          <table>
            <thead>
              <tr>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg. cost</th>
                <th>LTP</th>
                <th>Cur. val</th>
                <th>P&amp;L</th>
              </tr>
            </thead>

            <tbody>
              {allHoldings.map((stock) => {
                const curValue = stock.ltp * stock.qty;
                const pnl = curValue - stock.avgCost * stock.qty;
                const profClass = pnl >= 0 ? "profit" : "loss";

                return (
                  <tr key={stock._id}>
                    <td>{stock.instrument}</td>
                    <td>{stock.qty}</td>
                    <td>{stock.avgCost.toFixed(2)}</td>
                    <td>{stock.ltp.toFixed(2)}</td>
                    <td>{curValue.toFixed(2)}</td>
                    <td className={profClass}>{pnl.toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* ================= GRAPH ================= */}
      <div className="holdings-container">
        <VerticalGraph data={data} />
      </div>
    </>
  );
};

export default Holdings;
