import React, { useState, useEffect } from "react";
import axios from "axios";
import { VerticalGraph } from "./VerticalGraph";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3002/allHoldings").then((res) => {
      setAllHoldings(res.data);
    });
  }, []);

  const labels = allHoldings.map((stock) => stock.name);

  const data = {
    labels,
    datasets: [
      {
        label: "Stock Price",
        data: allHoldings.map((stock) => stock.price),
        backgroundColor: "rgba(65, 132, 243, 0.6)",
      },
    ],
  };

  return (
    <>
      {/* ================= HOLDINGS TABLE ================= */}
      <div className="holdings-container">
        <h3 className="title">Holdings ({allHoldings.length})</h3>

        <div className="order-table">
          <table>
            <thead>
              <tr>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg. cost</th>
                <th>LTP</th>
                <th>Cur. val</th>
                <th>P&amp;L</th>
                <th>Net chg.</th>
                <th>Day chg.</th>
              </tr>
            </thead>

            <tbody>
              {allHoldings.map((stock, index) => {
                const curValue = stock.price * stock.qty;
                const pnl = curValue - stock.avg * stock.qty;
                const isProfit = pnl >= 0;
                const profClass = isProfit ? "profit" : "loss";
                const dayClass = stock.isLoss ? "loss" : "profit";

                return (
                  <tr key={index}>
                    <td data-label="Instrument">{stock.name}</td>
                    <td data-label="Qty">{stock.qty}</td>
                    <td data-label="Avg. cost">{stock.avg.toFixed(2)}</td>
                    <td data-label="LTP">{stock.price.toFixed(2)}</td>
                    <td data-label="Cur. val">{curValue.toFixed(2)}</td>
                    <td data-label="P&L" className={profClass}>
                      {pnl.toFixed(2)}
                    </td>
                    <td data-label="Net chg." className={profClass}>
                      {stock.net}
                    </td>
                    <td data-label="Day chg." className={dayClass}>
                      {stock.day}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* ================= SUMMARY ================= */}
      <div className="holdings-summary">
        <div className="row text-center">
          <div className="col">
            <h5>
              29,875.<span>55</span>
            </h5>
            <p>Total investment</p>
          </div>

          <div className="col">
            <h5>
              31,428.<span>95</span>
            </h5>
            <p>Current value</p>
          </div>

          <div className="col">
            <h5 className="profit">1,553.40 (+5.20%)</h5>
            <p>P&amp;L</p>
          </div>
        </div>
      </div>

      {/* ================= GRAPH ================= */}
      <div className="holdings-container">
        <VerticalGraph data={data} />
      </div>
    </>
  );
};

export default Holdings;
