import React, { useState, useEffect } from "react";
import axios from "axios";
import { VerticalGraph } from "./VerticalGraph";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3002/allHoldings").then((res) => {
      setAllHoldings(res.data);
    });
  }, []);

  const labels = allHoldings.map((h) => h.name);

  const data = {
    labels,
    datasets: [
      {
        label: "Stock Price",
        data: allHoldings.map((h) => h.price),
      },
    ],
  };

  return (
    <div className="dashboard-content">
      <h3 className="mb-4">Holdings ({allHoldings.length})</h3>

      {/* TABLE */}
      <div className="table-card">
        <table className="holdings-table">
          <thead>
            <tr>
              <th>Instrument</th>
              <th className="right">Qty.</th>
              <th className="right">Avg. cost</th>
              <th className="right">LTP</th>
              <th className="right">Cur. val</th>
              <th className="right">P&L</th>
              <th className="right">Net chg.</th>
              <th className="right">Day chg.</th>
            </tr>
          </thead>

          <tbody>
            {allHoldings.map((stock, index) => {
              const curValue = stock.price * stock.qty;
              const pnl = curValue - stock.avg * stock.qty;
              const isProfit = pnl >= 0;

              return (
                <tr key={index}>
                  <td className="instrument">{stock.name}</td>
                  <td className="right">{stock.qty}</td>
                  <td className="right">{stock.avg.toFixed(2)}</td>
                  <td className="right">{stock.price.toFixed(2)}</td>
                  <td className="right">{curValue.toFixed(2)}</td>
                  <td className={`right ${isProfit ? "profit" : "loss"}`}>
                    {pnl.toFixed(2)}
                  </td>
                  <td className={`right ${isProfit ? "profit" : "loss"}`}>
                    {stock.net}
                  </td>
                  <td className={`right ${stock.isLoss ? "loss" : "profit"}`}>
                    {stock.day}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* SUMMARY */}
      <div className="summary-row">
        <div>
          <h5>29,875.<span>55</span></h5>
          <p>Total investment</p>
        </div>
        <div>
          <h5>31,428.<span>95</span></h5>
          <p>Current value</p>
        </div>
        <div>
          <h5 className="profit">1,553.40 (+5.20%)</h5>
          <p>P&amp;L</p>
        </div>
      </div>

      {/* GRAPH */}
      <div className="graph-card">
        <VerticalGraph data={data} />
      </div>
    </div>
  );
};

export default Holdings;
